$('.section-previews a').click( function(e) {
	e.preventDefault();

	let selected = this.getAttribute('href').replace('#','');
	let section = document.getElementById(selected);

	console.log('Section: ' + section);

	window.scroll({
	  top: 0, 
	  left: 0, 
	  behavior: 'smooth'
	});

	$('.section-previews').addClass('hide');
	$('#sections').removeClass('d-none');
	$(section).removeClass('d-none').addClass('active');
	setTimeout(function(){
		$('.section-previews').addClass('d-none');
	}, 251);

	var waypoint = new Waypoint({
	  element: document.getElementById('bio-nav'),
	  handler: function(direction) {
	    if(direction == 'down') {
		    $('#open-section-previews').addClass('hide');
				setTimeout(function(){
					$('#open-section-previews').addClass('d-none');
				}, 251);
			}
			if(direction == 'up') {
				$('#open-section-previews').removeClass('d-none');
				setTimeout(function(){
					$('#open-section-previews').removeClass('hide');
				}, 251);
			}
	  },
	  offset: 'bottom-in-view' 
	});
});

$('a.next').click( function(e) {
	e.preventDefault();

	let active = $('section.active');
	let selected = this.getAttribute('href').replace('#','');
	let section = document.getElementById(selected);

	window.scroll({
	  top: 0, 
	  left: 0, 
	  behavior: 'smooth'
	});

	$(active).removeClass('active').addClass('d-none');
	$(section).removeClass('d-none').addClass('active');
});

$('#bio-nav .btn-secondary').click( function(e) {
	e.preventDefault();

	window.scroll({
	  top: 0, 
	  left: 0, 
	  behavior: 'smooth'
	});

	$('.section-previews').removeClass('hide');
	$('.section-previews').removeClass('d-none');

	let active = $('section.active');
	$(active).removeClass('active').addClass('d-none');
	
	setTimeout(function(){
		$('#sections').addClass('d-none');
	}, 251);
});